package Problems;

public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	int n=5,r=1,totl_st,cnt_str,i;
	while(r<=n){
		totl_st=r;cnt_str=1;
		while(cnt_str<=totl_st) {
			System.out.print("* ");
			cnt_str++;
		}
		
		System.out.println();
		r++;
		}
	}

}
